puts "1. Combien d'handles contiennent un tiret -?"

puts "2. Combien d'handles contiennent un point.?"

puts "3. Quel est le handle le plus long de ce array?"

puts


choice = gets.chomp.to_i


case choice

when 1

  # Combien d'handles contiennent un tiret -?

  dash_handles = handles.select { |handle| handle.include?("-") }.size

  puts "Il y a #{dash_handles} handles qui contiennent un tiret -."

  puts

  puts

when 2

  # Combien d'handles contiennent un point.?

  dot_handles = handles.select { |handle| handle.include?(".") }.size

  puts "Il y a #{dot_handles} handles qui contiennent un point."

  puts

  puts

when 3

  # Quel est le handle le plus long de ce array?

  longest_handle = handles.max_by { |handle| handle.size }

  puts "Le handle le plus long est : #{longest_handle}"

  puts

  puts

else

  puts "Erreur, choix invalide."

end
